VALUES(2273, 'Oh and Harry don''t forget. When you''re done, just give it a tap and say:', 85, 19, 27);
VALUES(2274, 'Mischief managed. Otherwise, anyone can read it.', 85, 14, 27);
VALUES(2275, 'Mischief managed. Otherwise, anyone can read it.', 85, 19, 27);
VALUES(2276, 'Now, how much do you want? Delicious.', 86, 126, 19);
VALUES(2277, 'It''s meant to be the most haunted building in Britain. Did I mention that?', 86, 3, 19);
VALUES(2278, 'Twice.', 86, 2, 19);
VALUES(2279, 'Do you want to move a bit closer?', 86, 3, 19);
VALUES(2280, 'To the Shrieking Shack?', 86, 2, 19);
VALUES(2281, 'Actually, I''m fine here.', 86, 2, 19);
VALUES(2282, 'Well, well. Look who''s here. You two shopping for your new dream home? Bit grand for you, isn''t it, Weasle-Bee? Don''t your family sleep in uh... one room?', 86, 12, 19);
VALUES(2283, 'Shut your mouth, Malfoy.', 86, 2, 19);
VALUES(2284, 'Uh, Not very friendly. Boys, I think it''s time we teach Weasle-Bee how to respect his superiors.', 86, 12, 19);
VALUES(2285, 'Hope you don''t mean yourself!', 86, 3, 19);
VALUES(2286, 'How dare you talk to me! You filthy little mudblood! Who is that? Don''t stand there! Do something!', 86, 12, 19);
VALUES(2287, 'What?', 86, 80, 19);
