VALUES(2659, 'Oh, yes. Indeed, indeed.', 99, 17, 40);
VALUES(2660, 'And all the strawberries, you see?.', 99, 4, 40);
VALUES(2661, 'Come on, Buckbeak. Come on.', 99, 3, 40);
VALUES(2662, 'I see no strawberries.', 99, 17, 40);
VALUES(2663, 'Over there.', 99, 4, 40);
VALUES(2664, 'Where?', 99, 17, 40);
VALUES(2665, 'Over there.', 99, 4, 40);
VALUES(2666, 'This way.', 99, 1, 40);
VALUES(2667, 'Let''s get this over, please.', 99, 17, 40);
VALUES(2668, 'All right.', 99, 4, 40);
VALUES(2669, 'But where is it? I saw the beast, just now. Not a moment ago!', 99, 17, 40);
VALUES(2670, 'How extraordinary.', 99, 4, 40);
VALUES(2671, 'Buckbeak.', 99, 5, 40);
VALUES(2672, 'Come now, Dumbledore. Someone''s obviously released him.', 99, 17, 40);
VALUES(2673, 'Hagrid?', 99, 17, 40);
VALUES(2674, 'Buckbeak.', 99, 3, 40);
VALUES(2675, 'I don''t think the minister''s suggesting you had anything to do with this. How could you? You''ve been with us all the time.', 99, 4, 40);
VALUES(2676, 'Right. Well, well. We must search the grounds.', 99, 17, 40);
VALUES(2677, 'Well, search the skies, if you must, minister. Meanwhile, I''d like a nice cup of tea or a large brandy. Oh, executioner, your services are no longer required. Thank you.', 99, 4, 40);
VALUES(2678, 'You''ll find no small glasses in this house, professor.', 99, 5, 40);
VALUES(2679, 'Come on. This way. This way, now. Now what?', 99, 1, 40);
VALUES(2680, 'We save Sirius.', 99, 3, 67);
VALUES(2681, 'How?', 99, 1, 67);
VALUES(2682, 'No idea. Look. It''s Lupin.', 99, 3, 67);
VALUES(2683, 'Immobulus!', 99, 11, 67);
VALUES(2684, 'And Snape''s coming.', 99, 3, 67);
VALUES(2685, 'And now we wait.', 99, 1, 67);
VALUES(2686, 'And now we wait.', 99, 3, 67);
VALUES(2687, 'At least someone''s enjoying himself.', 100, 3, 67);
VALUES(2688, 'Yeah. Hermione?', 100, 1, 67);
VALUES(2689, 'Yeah?', 100, 3, 67);
VALUES(2690, 'Before, down by the lake, when I was with Sirius... I did see someone. That someone made the dementors go away.', 100, 1, 67);
