VALUES(2692, 'It was my dad. My dad conjured the Patronus.', 100, 1, 67);
VALUES(2693, 'Harry, but your dad''s... Dead. I know. I''m just telling you what I saw.', 100, 3, 67);
VALUES(2694, 'Here we come.', 100, 3, 67);
