VALUES(3409, 'Bones of the father, unwillingly given.', 126, 44, 14);
VALUES(3410, 'Flesh of the servent willingly sacrificed.', 126, 44, 14);
VALUES(3411, 'Blood of the enemy forcibly taken.', 126, 44, 14);
VALUES(3412, 'The dark lord shall rise again.', 126, 44, 14);
