VALUES(3446, 'Not so much now.', 129, 1, 30);
VALUES(3447, 'I''d better take a look at it.', 129, 13, 30);
VALUES(3448, 'The cup was a portkey. Someone had bewitched it.', 129, 1, 30);
VALUES(3449, 'What was it like? What was he like?', 129, 13, 30);
