VALUES(5878, 'Saint-like... You see, I''m holy. Holey, Fred, geddit?', 188, 19, 17);
VALUES(5879, 'With the whole wide world of ear- related humor at your disposal, you go for holey?  Pathetic.', 188, 14, 17);
VALUES(5880, 'Reckon I''m still better-looking than you.  Better-looking than Bill, that''s for sure.', 188, 19, 17);
VALUES(5881, 'Mad-Eye''s dead.', 188, 73, 17);
