VALUES(5887, 'I trust you with my life, Hagrid. I trust everyone in this room. Understood?', 188, 1, 17);
VALUES(5888, '''Ear, ''ear.', 188, 5, 17);
VALUES(5889, 'Better.', 188, 11, 17);
VALUES(6671, 'A Stinging Jinx.', 205, 3, 68);
VALUES(5890, 'No!  No!  I believed a different wand would work, I swear!', 188, 43, 17);
VALUES(5891, 'Then explain this!', 188, 9, 17);
VALUES(5892, 'But it makes no sense...', 188, 43, 17);
VALUES(5893, 'Perhaps our friend''s loyalties lie elsewhere, Wormtail.', 188, 9, 17);
VALUES(5894, 'No!  There must be a way!  I''ll think of something else!', 188, 43, 17);
